        WHEN x = 5235     THEN 'Real Estate Department'
        WHEN x = 5527     THEN 'Dubai Aviation City Corporation'
        WHEN x = 6566     THEN 'Dubai Silicon Oasis Authority'
        WHEN x = 6666     THEN 'Meydan City Corporation'
        WHEN x = 615504   THEN 'Dubai Integrated Economic Zones Authority'
        WHEN x = 616289   THEN 'FRH Real Estate Investment Limited'
        WHEN x = 4249418  THEN 'Trakheesi'
